tinymce.PluginManager.add('inserttag', function (editor, url) {

    editor.addButton('inserttag', {
        type: 'listbox',
        text: 'Tags',
        icon: false,
        onselect: function (e) {
            editor.insertContent(this.value());
            this.value(null);
            this.text('Tags');
        },
        values: editor.getParam("tags"),

    });

});