tinymce.PluginManager.add('inserttag', function (editor, url) {

    editor.addButton('inserttag', {
        type: 'listbox',
        text: 'Tags',
        icon: false,
        onselect: function (e) {
            let selfValue = this.value();
            let isString = typeof selfValue === 'string' || selfValue instanceof String;
            editor.insertContent(isString ? selfValue : selfValue.toString());
            this.value(null);
            this.text('Tags');
        },
        values: editor.getParam("tags"),

    });

});